package Loops;
import java.util.Scanner;

public class for_loop {

	public static void main(String[] args) {
		
		
		
		int i,n;
		System.out.println("enter the limit  :");
		Scanner num = new Scanner(System.in);
	    n=num.nextInt();
		for(i=2;i<=n;i+=2) {
			System.out.println(+i);
		}
	
		// TODO Auto-generated method stub

	}

}
